Config:
  function showWindowX(app, ownConfig){
      if(Ext.isEmpty(app) || Ext.isEmpty(ownConfig))
        return;
      
      var desk = app.getDesktop();
      if(typeof desk !== 'undefined') {
        var win = desk.getWindow(ownConfig.id);
        if(!win){
          win = desk.createWindow(ownConfig);
        }
        win.show();
      }
  }
